<script setup>
import {onMounted, ref} from "vue"

const activeHouse = ref(null)
const activeHousePart = ref(null)
const btnLabel = ref('Выбрать секцию на схеме');
const isShow = ref(false)

function toggleViewOfWindow() {
  isShow.value = !isShow.value
}
function setActiveHouse(e) {
  if (e.currentTarget.dataset.houseNumber && e.target.dataset.housePart) {
    activeHouse.value = Number(e.currentTarget.dataset.houseNumber)
    activeHousePart.value = Number(e.target.dataset.housePart)
    btnLabel.value = `Корпус ${activeHouse.value} Секция ${activeHousePart.value}`
  }
}

onMounted(() => {
  window.addEventListener('click', (e) => {
    if (e.target.closest('.building-filter')) return
    if (!e.target.closest('.building-filter__window')) {
      isShow.value = false
    }
  })
})


</script>

